$(".go_to_home").click(function () {
    $("html, body").animate({
        scrollTop: 0
    }, 300);
    return false;
});

$(document).mouseup(function(e){
   var div = $('.overlay-map');
    if (!div.is(e.target) && div.has(e.target).length === 0){
        div.show();
    }
    else {
        div.hide();
    }
});